import React, { useState, useEffect } from 'react';  // Импорт React и useState/useEffect
import axios from 'axios';  // Импорт axios для HTTP-запросов
import './App.css';  // Импорт стилей

function App() {
  const [text, setText] = useState('');  // Состояние для исходного текста
  const [encryptedText, setEncryptedText] = useState('');  // Состояние для зашифрованного текста
  const [decryptedText, setDecryptedText] = useState('');  // Состояние для расшифрованного текста
  const [encryptedSessionKey, setEncryptedSessionKey] = useState('');  // Состояние для зашифрованного ключа
  const [result, setResult] = useState('');  // Состояние для промежуточного результата
  const [publicKey, setPublicKey] = useState('');  // Состояние для публичного ключа
  const [privateKey, setPrivateKey] = useState('');  // Состояние для приватного ключа
  const [error, setError] = useState('');  // Состояние для ошибок
  const [showFullPublicKey, setShowFullPublicKey] = useState(false);  // Состояние для отображения полного публичного ключа
  const [showFullPrivateKey, setShowFullPrivateKey] = useState(false);  // Состояние для отображения полного приватного ключа
  const [digest, setDigest] = useState('');  // Состояние для дайджеста
  const [hash, setHash] = useState('');  // Состояние для хеша
  const [signature, setSignature] = useState('');  // Состояние для подписи
  const [encryptFile, setEncryptFile] = useState(null);  // Состояние для файла для шифрования
  const [decryptFile, setDecryptFile] = useState(null);  // Состояние для файла для расшифровки

  useEffect(() => {
    // Эффект для загрузки ключей при монтировании компонента
    const fetchKeys = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/generate-keys', {
          headers: { 'Content-Type': 'application/json' },
        });
        console.log('Keys response:', response.data);  // Логирование ответа
        setPublicKey(response.data.rsa_public_key);  // Установка публичного ключа
        setPrivateKey(response.data.rsa_private_key);  // Установка приватного ключа
        setError('');  // Очистка ошибок
      } catch (error) {
        console.error('Error fetching keys:', error.response ? error.response.data : error.message);  // Логирование ошибки
        setError(`Failed to load keys. Details: ${error.response ? JSON.stringify(error.response.data) : error.message}`);  // Установка сообщения об ошибке
      }
    };
    fetchKeys();
  }, []);  // Пустой массив зависимостей для выполнения только при монтировании

  const generateDigest = (text) => {
    // Генерация дайджеста на основе текста
    if (!text) return '';
    return text
      .split(' ')  // Разделение текста на слова
      .map(word => {
        let result = '';
        for (let i = 2; i < word.length; i += 3) {  // Взятие символов с шагом 3, начиная с 2
          result += word[i] || '';
        }
        return result;
      })
      .join('');  // Объединение результатов
  };

  const handleEncrypt = async (event) => {
    // Обработчик события шифрования
    event.preventDefault();  // Предотвращение стандартного поведения формы
    if (!publicKey || !privateKey) {
      setError('Ключи не сгенерированы. Пожалуйста, подождите...');  // Проверка наличия ключей
      return;
    }
    let calculatedDigest = '';
    let fileText = '';
    if (encryptFile) {  // Обработка файла
      const reader = new FileReader();
      reader.onload = async (e) => {
        fileText = e.target.result;  // Чтение текста из файла
        calculatedDigest = generateDigest(fileText);  // Вычисление дайджеста
        setDigest(calculatedDigest);  // Установка дайджеста
        try {
          const formData = new FormData();  // Формирование данных для отправки
          formData.append('file', encryptFile);  // Добавление файла
          const response = await axios.post('http://localhost:5000/api/encrypt', formData, {
            headers: { 'Content-Type': 'multipart/form-data' },  // Установка заголовка
            responseType: 'blob',  // Ожидание файла как ответа
          });
          const url = window.URL.createObjectURL(response.data);  // Создание URL для скачивания
          const link = document.createElement('a');  // Создание ссылки
          link.href = url;
          link.download = 'encrypted_data.txt';  // Установка имени файла
          document.body.appendChild(link);
          link.click();  // Инициирование скачивания
          document.body.removeChild(link);  // Удаление ссылки
          window.URL.revokeObjectURL(url);  // Освобождение ресурсов
          setEncryptedText('');  // Очистка полей
          setEncryptedSessionKey('');
          setSignature('');
          setError('');
        } catch (error) {
          console.error('Error:', error.response ? error.response.data : error.message);  // Логирование ошибки
          setError(`Ошибка. Детали: ${error.response ? JSON.stringify(error.response.data) : error.message}`);  // Установка ошибки
        }
      };
      reader.readAsText(encryptFile);  // Чтение файла как текста
    } else {  // Обработка текста
      calculatedDigest = generateDigest(text);  // Вычисление дайджеста
      setDigest(calculatedDigest);  // Установка дайджеста
      try {
        const response = await axios.post('http://localhost:5000/api/encrypt', {
          text: text,
          digest: calculatedDigest,
        }, {
          headers: { 'Content-Type': 'application/json' },  // Установка заголовка
          responseType: 'blob',  // Ожидание файла как ответа
        });
        const url = window.URL.createObjectURL(response.data);  // Создание URL для скачивания
        const link = document.createElement('a');  // Создание ссылки
        link.href = url;
        link.download = 'encrypted_data.txt';  // Установка имени файла
        document.body.appendChild(link);
        link.click();  // Инициирование скачивания
        document.body.removeChild(link);  // Удаление ссылки
        window.URL.revokeObjectURL(url);  // Освобождение ресурсов
        setEncryptedText('');  // Очистка полей
        setEncryptedSessionKey('');
        setSignature('');
        setError('');
      } catch (error) {
        console.error('Error:', error.response ? error.response.data : error.message);  // Логирование ошибки
        setError(`Ошибка. Детали: ${error.response ? JSON.stringify(error.response.data) : error.message}`);  // Установка ошибки
      }
    }
  };

  const handleDecrypt = async (event) => {
    // Обработчик события расшифровки
    event.preventDefault();  // Предотвращение стандартного поведения формы
    if (!encryptedText && !decryptFile) {
      setError('Введите зашифрованный текст или загрузите файл');  // Проверка наличия данных
      return;
    }
    let encryptedSessionKey, encryptedTextPart;
    if (decryptFile) {  // Обработка файла
      const reader = new FileReader();
      reader.onload = async (e) => {
        const fileContent = e.target.result.trim();  // Чтение и очистка данных
        [encryptedSessionKey, encryptedTextPart] = fileContent.split('||');  // Разделение данных
        if (!encryptedSessionKey || !encryptedTextPart) {
          setError('Некорректный формат файла, ожидаемый encrypted_session_key||encrypted_text');  // Проверка формата
          return;
        }
        try {
          const formData = new FormData();  // Формирование данных для отправки
          const file = new Blob([fileContent], { type: 'text/plain' });  // Создание файла
          formData.append('file', file, 'encrypted_data.txt');  // Добавление файла
          const response = await axios.post('http://localhost:5000/api/decrypt', formData, {
            headers: { 'Content-Type': 'multipart/form-data' },  // Установка заголовка
          });
          const { decrypted, signature_valid } = response.data;  // Извлечение результата
          setDecryptedText(`Decrypted: ${decrypted}, Signature Valid: ${signature_valid}`);  // Установка расшифрованного текста
          setError('');
        } catch (error) {
          console.error('Error decrypting:', error.response ? error.response.data : error.message);  // Логирование ошибки
          setError(`Ошибка дешифрования. Детали: ${error.response ? JSON.stringify(error.response.data) : error.message}`);  // Установка ошибки
        }
      };
      reader.readAsText(decryptFile);  // Чтение файла как текста
    } else {  // Обработка текста
      [encryptedSessionKey, encryptedTextPart] = result.split('||');  // Разделение данных
      if (!encryptedSessionKey || !encryptedTextPart) {
        setError('Некорректный формат зашифрованного текста');  // Проверка формата
        return;
      }
      try {
        const response = await axios.post('http://localhost:5000/api/decrypt', {
          encrypted_text: encryptedTextPart,
          encrypted_session_key: encryptedSessionKey,
          signature: signature,
          digest: digest,
        }, {
          headers: { 'Content-Type': 'application/json' },  // Установка заголовка
        });
        const { decrypted, signature_valid } = response.data;  // Извлечение результата
        setDecryptedText(`Decrypted: ${decrypted}, Signature Valid: ${signature_valid}`);  // Установка расшифрованного текста
        setError('');
      } catch (error) {
        console.error('Error decrypting:', error.response ? error.response.data : error.message);  // Логирование ошибки
        setError(`Ошибка дешифрования. Детали: ${error.response ? JSON.stringify(error.response.data) : error.message}`);  // Установка ошибки
      }
    }
  };

  const exportKey = (key, keyType) => {
    // Экспорт ключа в файл
    const blob = new Blob([key], { type: 'text/plain' });  // Создание Blob объекта
    const url = window.URL.createObjectURL(blob);  // Создание URL
    const link = document.createElement('a');  // Создание ссылки
    link.href = url;
    link.download = `${keyType}_key.pem`;  // Установка имени файла
    document.body.appendChild(link);
    link.click();  // Инициирование скачивания
    document.body.removeChild(link);  // Удаление ссылки
    window.URL.revokeObjectURL(url);  // Освобождение ресурсов
  };

  const copyToClipboard = (key) => {
    // Копирование текста в буфер обмена
    navigator.clipboard.writeText(key).then(() => {
      alert('Copied to clipboard!');  // Успешное уведомление
    }).catch(err => {
      setError(`Failed to copy: ${err.message}`);  // Обработка ошибки
    });
  };

  const exportDigest = () => {
    // Экспорт дайджеста в файл
    const blob = new Blob([digest], { type: 'text/plain' });  // Создание Blob объекта
    const url = window.URL.createObjectURL(blob);  // Создание URL
    const link = document.createElement('a');  // Создание ссылки
    link.href = url;
    link.download = 'digest.txt';  // Установка имени файла
    document.body.appendChild(link);
    link.click();  // Инициирование скачивания
    document.body.removeChild(link);  // Удаление ссылки
    window.URL.revokeObjectURL(url);  // Освобождение ресурсов
  };

  const copyDigestToClipboard = () => {
    // Копирование дайджеста в буфер обмена
    navigator.clipboard.writeText(digest).then(() => {
      alert('Digest copied to clipboard!');  // Успешное уведомление
    }).catch(err => {
      setError(`Failed to copy digest: ${err.message}`);  // Обработка ошибки
    });
  };

  const handleImportKey = (event, keyType) => {
    // Обработчик импорта ключа
    const file = event.target.files[0];  // Получение файла
    if (file) {
      const reader = new FileReader();  // Создание объекта для чтения файла
      reader.onload = (e) => {
        const keyContent = e.target.result;  // Чтение содержимого файла
        try {
          if (keyType === 'public' && !keyContent.includes('-----BEGIN RSA PUBLIC KEY-----')) {
            throw new Error('Неверный формат публичного ключа');  // Проверка формата
          }
          if (keyType === 'private' && !keyContent.includes('-----BEGIN RSA PRIVATE KEY-----')) {
            throw new Error('Неверный формат приватного ключа');  // Проверка формата
          }
          if (keyType === 'public') {
            setPublicKey(keyContent);  // Установка публичного ключа
          } else if (keyType === 'private') {
            setPrivateKey(keyContent);  // Установка приватного ключа
          }
          setError('');  // Очистка ошибок
        } catch (err) {
          setError(`Ошибка импорта: ${err.message}`);  // Установка ошибки
        }
      };
      reader.readAsText(file);  // Чтение файла как текста
      event.target.value = '';  // Очистка поля файла
    }
  };

  const exportResults = () => {
    // Экспорт всех результатов в JSON-файл
    const results = {
      digest,
      hash,
      signature,
      encryptedSessionKey,
      encryptedText,
      result,
    };
    const blob = new Blob([JSON.stringify(results, null, 2)], { type: 'application/json' });  // Создание Blob объекта
    const url = window.URL.createObjectURL(blob);  // Создание URL
    const link = document.createElement('a');  // Создание ссылки
    link.href = url;
    link.download = 'crypto_results.json';  // Установка имени файла
    document.body.appendChild(link);
    link.click();  // Инициирование скачивания
    document.body.removeChild(link);  // Удаление ссылки
    window.URL.revokeObjectURL(url);  // Освобождение ресурсов
  };

  const handleEncryptFileChange = (event) => {
    // Обработчик изменения файла для шифрования
    setEncryptFile(event.target.files[0]);  // Установка файла
  };

  const handleDecryptFileChange = (event) => {
    // Обработчик изменения файла для расшифровки
    setDecryptFile(event.target.files[0]);  // Установка файла
  };

  return (
    <div className="App">  {/* Основной контейнер приложения */}
      <h1>Crypto App</h1>  {/* Заголовок приложения */}

      <div className="keys-section">  {/* Секция ключей */}
        <h3>Keys</h3>  {/* Заголовок секции */}
        <p>
          <strong>Public Key:</strong> {showFullPublicKey ? publicKey : `${publicKey.substring(0, 50)}...`}
          <button className="show-full-btn" onClick={() => setShowFullPublicKey(!showFullPublicKey)}>
            {showFullPublicKey ? 'Скрыть' : 'Показать полностью'}
          </button>
        </p>
        <button onClick={() => exportKey(publicKey, 'public')} style={{ marginRight: '10px' }}>
          Export Public Key
        </button>
        <button onClick={() => copyToClipboard(publicKey)} style={{ marginRight: '10px' }}>
          Copy Public Key
        </button>
        <label>
          Import Public Key
          <input type="file" onChange={(e) => handleImportKey(e, 'public')} accept=".pem" />
        </label>
        <p>
          <strong>Private Key:</strong> {showFullPrivateKey ? privateKey : `${privateKey.substring(0, 50)}...`}
          <button className="show-full-btn" onClick={() => setShowFullPrivateKey(!showFullPrivateKey)}>
            {showFullPrivateKey ? 'Скрыть' : 'Показать полностью'}
          </button>
        </p>
        <button onClick={() => exportKey(privateKey, 'private')} style={{ marginRight: '10px' }}>
          Export Private Key
        </button>
        <button onClick={() => copyToClipboard(privateKey)} style={{ marginRight: '10px' }}>
          Copy Private Key
        </button>
        <label>
          Import Private Key
          <input type="file" onChange={(e) => handleImportKey(e, 'private')} accept=".pem" />
        </label>
        {error && <p className="error"><strong>Error:</strong> {error}</p>}
      </div>

      <div className="encrypt-section">  {/* Секция шифрования */}
        <h3>Encrypt</h3>  {/* Заголовок секции */}
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text to encrypt"
        />
        <label>
          or Upload File
          <input type="file" onChange={handleEncryptFileChange} accept=".txt" />
        </label>
        <button onClick={handleEncrypt}>Encrypt</button>
        <p><strong>Digest:</strong> {digest}</p>
        <p><strong>Hash:</strong> {hash}</p>
        <p><strong>Signature:</strong> {signature}</p>
        <p><strong>Encrypted Session Key:</strong> {encryptedSessionKey}</p>
        <p><strong>Encrypted Text:</strong> {encryptedText}</p>
        <button onClick={exportDigest} style={{ marginRight: '10px' }}>
          Export Digest
        </button>
        <button onClick={copyDigestToClipboard} style={{ marginRight: '10px' }}>
          Copy Digest
        </button>
        <button onClick={exportResults}>
          Export All Results
        </button>
      </div>

      <div className="decrypt-section">  {/* Секция расшифровки */}
        <h3>Decrypt</h3>  {/* Заголовок секции */}
        <p><strong>Encrypted Text:</strong> {encryptedText}</p>  {/* Отображение зашифрованного текста */}
        <input
          type="text"
          value={encryptedText}
          onChange={(e) => setEncryptedText(e.target.value)}
          placeholder="Enter encrypted text"
        />
        <label>
          or Upload Encrypted File
          <input type="file" onChange={handleDecryptFileChange} accept=".txt" />
        </label>
        <button onClick={handleDecrypt}>Decrypt</button>
      </div>

      <div className="result-section">  {/* Секция результата */}
        <p><strong>Decrypted Text:</strong> {decryptedText}</p>  {/* Отображение расшифрованного текста */}
      </div>
    </div>
  );
}

export default App;  // Экспорт компонента
