import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Оставим стили по умолчанию

function App() {
  const [text, setText] = useState(''); // Текст для шифрования
  const [encryptedText, setEncryptedText] = useState(''); // Зашифрованный текст для дешифрования
  const [result, setResult] = useState(''); // Результат (зашифрованный или расшифрованный текст)
  const [publicKey, setPublicKey] = useState(''); // Публичный ключ
  const [privateKey, setPrivateKey] = useState(''); // Приватный ключ

  // Загрузка ключей при монтировании компонента
  useEffect(() => {
    const fetchKeys = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/generate-keys');
        setPublicKey(response.data.public_key);
        setPrivateKey(response.data.private_key);
      } catch (error) {
        console.error('Error fetching keys:', error);
        setResult('Failed to load keys');
      }
    };
    fetchKeys();
  }, []);

  // Обработчик шифрования
  const handleEncrypt = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/encrypt', {
        text: text,
      }, {
        headers: { 'Content-Type': 'application/json' },
      });
      setEncryptedText(response.data.encrypted); // Сохраняем зашифрованный текст
      setResult(response.data.encrypted);
    } catch (error) {
      console.error('Error encrypting:', error);
      setResult('Encryption error');
    }
  };

  // Обработчик дешифрования
  const handleDecrypt = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/decrypt', {
        encrypted: encryptedText,
      }, {
        headers: { 'Content-Type': 'application/json' },
      });
      setResult(response.data.decrypted);
    } catch (error) {
      console.error('Error decrypting:', error);
      setResult('Decryption error');
    }
  };

  return (
    <div className="App" style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h1>Crypto App</h1>

      {/* Генерация и отображение ключей */}
      <div style={{ marginBottom: '20px' }}>
        <h3>Keys</h3>
        <p><strong>Public Key:</strong> {publicKey.substring(0, 50)}...</p>
        <p><strong>Private Key:</strong> {privateKey.substring(0, 50)}...</p>
        <p>(Only first 50 characters shown for brevity)</p>
      </div>

      {/* Шифрование */}
      <div style={{ marginBottom: '20px' }}>
        <h3>Encrypt</h3>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text to encrypt"
          style={{ padding: '8px', width: '70%', marginRight: '10px' }}
        />
        <button onClick={handleEncrypt} style={{ padding: '8px' }}>
          Encrypt
        </button>
      </div>

      {/* Дешифрование */}
      <div style={{ marginBottom: '20px' }}>
        <h3>Decrypt</h3>
        <input
          type="text"
          value={encryptedText}
          onChange={(e) => setEncryptedText(e.target.value)}
          placeholder="Enter encrypted text (or use above result)"
          style={{ padding: '8px', width: '70%', marginRight: '10px' }}
        />
        <button onClick={handleDecrypt} style={{ padding: '8px' }}>
          Decrypt
        </button>
      </div>

      {/* Результат */}
      <p><strong>Result:</strong> {result}</p>
    </div>
  );
}

export default App;
