import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [text, setText] = useState('');
  const [encryptedText, setEncryptedText] = useState('');
  const [result, setResult] = useState('');
  const [publicKey, setPublicKey] = useState(localStorage.getItem('publicKey') || '');
  const [privateKey, setPrivateKey] = useState(localStorage.getItem('privateKey') || '');
  const [error, setError] = useState('');

  useEffect(() => {
    // Сохраняем ключи в localStorage при изменении
    if (publicKey) localStorage.setItem('publicKey', publicKey);
    if (privateKey) localStorage.setItem('privateKey', privateKey);

    // Генерируем ключи только если их нет
    if (!publicKey || !privateKey) {
      const fetchKeys = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/generate-keys', {
            headers: { 'Content-Type': 'application/json' },
          });
          console.log('Keys response:', response.data);
          setPublicKey(response.data.rsa_public_key);
          setPrivateKey(response.data.rsa_private_key);
          setError('');
        } catch (error) {
          console.error('Error fetching keys:', error.response ? error.response.data : error.message);
          setError(`Failed to load keys. Details: ${error.response ? JSON.stringify(error.response.data) : error.message}`);
        }
      };
      fetchKeys();
    }
  }, [publicKey, privateKey]);

  const handleEncrypt = async () => {
    if (!publicKey || !privateKey) {
      setError('Ключи не сгенерированы. Пожалуйста, подождите...');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/api/encrypt', {
        text: text,
      }, {
        headers: { 'Content-Type': 'application/json' },
      });
      setEncryptedText(response.data.encrypted);
      setResult(response.data.encrypted);
      setError('');
    } catch (error) {
      console.error('Error encrypting:', error.response ? error.response.data : error.message);
      setError(`Ошибка шифрования. Детали: ${error.response ? JSON.stringify(error.response.data) : error.message}`);
    }
  };

  const handleDecrypt = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/decrypt', {
        encrypted: encryptedText,
      }, {
        headers: { 'Content-Type': 'application/json' },
      });
      setResult(response.data.decrypted);
      setError('');
    } catch (error) {
      console.error('Error decrypting:', error.response ? error.response.data : error.message);
      setError(`Decryption error. Details: ${error.response ? JSON.stringify(error.response.data) : error.message}`);
    }
  };

  const exportKey = (key, keyType) => {
    const blob = new Blob([key], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `${keyType}_key.pem`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);
  };

  const copyToClipboard = (key) => {
    navigator.clipboard.writeText(key).then(() => {
      alert('Key copied to clipboard!');
    }).catch(err => {
      setError(`Failed to copy: ${err.message}`);
    });
  };

  const handleImportKey = (event, keyType) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const keyContent = e.target.result;
        if (keyType === 'public') {
          setPublicKey(keyContent);
        } else if (keyType === 'private') {
          setPrivateKey(keyContent);
        }
      };
      reader.readAsText(file);
      event.target.value = '';
    }
  };

  return (
    <div className="App">
      <h1>CryptoApp</h1>

      <div className="keys-section">
        <h3>Keys</h3>
        <p><strong>Public Key:</strong> {publicKey.substring(0, 50)}...</p>
        <button onClick={() => exportKey(publicKey, 'public')}>Export Public Key</button>
        <button onClick={() => copyToClipboard(publicKey)}>Copy Public Key</button>
        <label>
          Import Public Key
          <input type="file" onChange={(e) => handleImportKey(e, 'public')} accept=".pem" />
        </label>
        <p><strong>Private Key:</strong> {privateKey.substring(0, 50)}...</p>
        <button onClick={() => exportKey(privateKey, 'private')}>Export Private Key</button>
        <button onClick={() => copyToClipboard(privateKey)}>Copy Private Key</button>
        <label>
          Import Private Key
          <input type="file" onChange={(e) => handleImportKey(e, 'private')} accept=".pem" />
        </label>
        {error && <p className="error"><strong>Error:</strong> {error}</p>}
      </div>

      <div className="encrypt-section">
        <h3>Encrypt</h3>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text to encrypt"
        />
        <button onClick={handleEncrypt}>Encrypt</button>
      </div>

      <div className="decrypt-section">
        <h3>Decrypt</h3>
        <input
          type="text"
          value={encryptedText}
          onChange={(e) => setEncryptedText(e.target.value)}
          placeholder="Enter encrypted text"
        />
        <button onClick={handleDecrypt}>Decrypt</button>
      </div>

      <div className="result-section">
        <p><strong>Result:</strong> {result}</p>
      </div>
    </div>
  );
}

export default App;
