import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [text, setText] = useState('');
  const [encryptedText, setEncryptedText] = useState('');
  const [result, setResult] = useState('');
  const [publicKey, setPublicKey] = useState('');
  const [privateKey, setPrivateKey] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchKeys = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/generate-keys', {
          headers: { 'Content-Type': 'application/json' },
        });
        console.log('Keys response:', response.data);
        setPublicKey(response.data.rsa_public_key);
        setPrivateKey(response.data.rsa_private_key);
        setError('');
      } catch (error) {
        console.error('Error fetching keys:', error.message);
        setError('Failed to load keys. Check server or console.');
      }
    };
    fetchKeys();
  }, []);

  const handleEncrypt = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/encrypt', {
        text: text,
      }, {
        headers: { 'Content-Type': 'application/json' },
      });
      setEncryptedText(response.data.encrypted);
      setResult(response.data.encrypted);
      setError('');
    } catch (error) {
      console.error('Error encrypting:', error.message);
      setError('Encryption error. Check console.');
    }
  };

  const handleDecrypt = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/decrypt', {
        encrypted: encryptedText,
      }, {
        headers: { 'Content-Type': 'application/json' },
      });
      setResult(response.data.decrypted);
      setError('');
    } catch (error) {
      console.error('Error decrypting:', error.message);
      setError('Decryption error. Check console.');
    }
  };

  return (
    <div className="App" style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h1>Crypto App</h1>

      <div style={{ marginBottom: '20px' }}>
        <h3>Keys</h3>
        <p><strong>Public Key:</strong> {publicKey.substring(0, 50)}...</p>
        <p><strong>Private Key:</strong> {privateKey.substring(0, 50)}...</p>
        <p>(Only first 50 characters shown)</p>
        {error && <p style={{ color: 'red' }}><strong>Error:</strong> {error}</p>}
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h3>Encrypt</h3>
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text to encrypt"
          style={{ padding: '8px', width: '50%', marginRight: '10px' }}
        />
        <button onClick={handleEncrypt} style={{ padding: '8px' }}>
          Encrypt
        </button>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h3>Decrypt</h3>
        <input
          type="text"
          value={encryptedText}
          onChange={(e) => setEncryptedText(e.target.value)}
          placeholder="Enter encrypted text"
          style={{ padding: '8px', width: '50%', marginRight: '10px' }}
        />
        <button onClick={handleDecrypt} style={{ padding: '8px' }}>
          Decrypt
        </button>
      </div>

      <p><strong>Result:</strong> {result}</p>
    </div>
  );
}

export default App;
